import * as React from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableHighlight,
  TextInput,
  KeyboardAvoidingView,
} from "react-native";
// import { TextInput, TouchableOpacity } from "react-native-gesture-handler"; //Xingpeng: I think we should add dependency of this import into json file; npm i -s react-native-gesture-handler
import { NavigationContainer } from "@react-navigation/native";
import { TouchableOpacity } from "react-native-gesture-handler";

export default class LoginScreen extends React.Component {
  render() {
    return (
      <KeyboardAvoidingView style={styles.container} behavior="padding">
        {/* <Text style={{ fontSize: 25, marginTop: 20 }}>Welcome Back! </Text> */}
        {/* <Text style={{ fontSize: 16, color: "gray", marginTop: 20 }}>
          Sign in to continue
        </Text> */}

        <Text style={styles.TitleText}>Login</Text>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.textInput}
            placeholder="Email"
            // onChangeText={(text) => {
            //   this.setState({ errMsg: "" }), this.setState({ username: text });
            // }} // this one pop the error.
          />
          <TextInput
            style={styles.textInput}
            placeholder="Password "
            secureTextEntry
            // onChangeText={(text) => {
            //   this.setState({ errMsg: "" }), this.setState({ password: text });
            // }}
          />
        </View>
        <View
          style={{
            alignItems: "center",
            justifyContent: "center",
            marginTop: 40,
          }}
        >
          <TouchableHighlight
            // onPress={() => this.onLogin()}
            style={styles.button}
          >
            <Text style={styles.buttonTextWhite}>Login Now</Text>
          </TouchableHighlight>
        </View>
        <Text style={{ marginTop: 20 }}>Forgot Password ?</Text>

        <View style={{ flexDirection: "row", marginTop: 40 }}>
          <Text style={{ color: "gray" }}>Don't have an account?</Text>
          <Text style={{ fontWeight: "bold" }}> Sign Up</Text>
        </View>
      </KeyboardAvoidingView>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center",
  },
  inputContainer: {
    width: "50%",
    backgroundColor: "tomato",
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  TitleText: {
    textAlign: "center",
    color: "#072B4F",
    fontSize: 25,
    fontWeight: "bold",
    paddingBottom: 30,
  },
  buttonTextWhite: {
    textAlign: "center",
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
  },
  buttonTextDark: {
    textAlign: "center",
    color: "#072B4F",
    fontSize: 18,
    fontWeight: "bold",
  },
  textInput: {
    textAlign: "center",
    color: "#ddd",
    fontSize: 17,

    // marginTop: 40,
    // borderBottomColor: "#ddd",
    // borderBottomWidth: 2,
    // paddingBottom: 20,
  },
  button: {
    width: 250,
    height: 45,
    backgroundColor: "#072B4F",
    padding: 10,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 8,
    marginTop: 15,
  },
});
