import React, { useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableHighlight,
  TextInput,
  KeyboardAvoidingView,
} from "react-native";
import { KeyboardAwareScrollView } from "react-native-keyboard-aware-scroll-view";

// import { TextInput, TouchableOpacity } from "react-native-gesture-handler"; //Xingpeng: I think we should add dependency of this import into json file; npm i -s react-native-gesture-handler
import { NavigationContainer } from "@react-navigation/native";
import { TouchableOpacity } from "react-native-gesture-handler";

const [email, setEmail] = useState("");
const [password, setPassword] = useState("");

export default function LoginScreen() {
  return (
    <KeyboardAwareScrollView contentContainerStyle={styles.container}>
      <View style={[styles.frameContainer, styles.shadowProp]}>
        <Text style={styles.TitleText}>Login</Text>

        <View style={styles.inputContainer}>
          <TextInput
            style={styles.textInput}
            placeholder="Email"
            placeholderTextColor="gray"
            value={email}
            onChangeText={(text) => setEmail(text)}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.textInput}
            placeholder="Password"
            placeholderTextColor="gray"
            secureTextEntry
            value={password}
            onChangeText={(text) => setPassword(text)}
          />
        </View>
        <View
          style={{
            alignItems: "center",
            justifyContent: "center",
            marginTop: 40,
          }}
        >
          <TouchableHighlight
            // onPress={() => this.onLogin()}
            style={styles.button}
          >
            <Text style={styles.buttonTextWhite}>Login Now</Text>
          </TouchableHighlight>
        </View>
        <Text style={{ marginTop: 20 }}>Forgot Password ?</Text>

        <View style={{ flexDirection: "row", marginTop: 40 }}>
          <Text style={{ color: "gray" }}>Don't have an account?</Text>
          <Text style={{ fontWeight: "bold" }}> Sign Up</Text>
        </View>
      </View>
    </KeyboardAwareScrollView>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center",
  },
  frameContainer: {
    width: "80%",
    height: "50%",
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 16,
  },
  shadowProp: {
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.3,
    shadowRadius: 5,
  },
  TitleText: {
    textAlign: "center",
    color: "#072B4F",
    fontSize: 25,
    fontWeight: "bold",
    paddingBottom: 30,
    paddingTop: 30,
  },
  inputContainer: {
    width: "80%",
    borderBottomColor: "grey",
    borderBottomWidth: 1.5,
  },
  textInput: {
    width: "70%",
    color: "#072B4F",
    fontSize: 17,
    marginTop: 5,
    paddingVertical: 10,
    borderRadius: 10,
    borderBottomColor: "white",
  },
  button: {
    width: 250,
    height: 45,
    backgroundColor: "#072B4F",
    padding: 10,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 8,
    marginTop: 5,
  },
  buttonTextWhite: {
    textAlign: "center",
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
  },
  buttonTextDark: {
    textAlign: "center",
    color: "#072B4F",
    fontSize: 18,
    fontWeight: "bold",
  },
});
